mapper.select:
select tp.id as pid, tp.name as pname,
       tc.cid as cid, tc.name as cname
from t_province as tp left join t_city as tc on tp.id = tc.pid
where tc.is_used = 0

    and tp.id = #{pid}


    and tp.id = #{pid}

mapper.update:
update

    t_province set is_used = 1 where id = #{pid}


    t_city set is_used = 1 where pid = #{pid} and cid = #{cid}

update t_user
    set is_delete = 1
where role_pid = #{pid}

      and role_cid = #{cid}

update t_city
    set is_used = 0
where pid = #{pid}

      and cid = #{cid}

update t_province
    set is_used = 0
where id = #{pid}
