mapper.select:
select id,username,phone,role_pid,role_cid,is_lock
from t_user
where is_delete = 0

    and role_pid = #{pid}


    and role_cid > 0


    and (username = #{query.key} or phone = #{query.key})


    and is_lock = #{query.status}


    and role_pid = #{s_pid} and role_cid = #{s_cid}

order by role_cid asc
limit #{limit} offset #{offset}

select count(1)
from t_user
where is_delete = 0

    and role_pid = #{pid}


    and role_cid > 0


    and (username = #{query.key} or phone = #{query.key})


    and is_lock = #{query.status}


    and role_pid = #{s_pid} and role_cid = #{s_cid}
